module.exports = function(RED) {
    "use strict";

    function InformationServiceNode(config) {
        RED.nodes.createNode(this, config);
        var node = this;
        node.name = config.name;
        node.location = config.location;
        node.informationResource = config.informationResource;

        this.on("input", function(msg, send, done) {
            msg.name = node.name;
            msg.location = node.location;
            msg.informationResource = node.informationResource;

            send(msg);
            if (done) {
                done();
            }
        });
    }

    RED.nodes.registerType("Information Service", InformationServiceNode);
};