module.exports = function(RED) {
    "use strict";

    function SocialServiceNode(config) {
        RED.nodes.createNode(this, config);
        var node = this;
        node.name = config.name;
        node.location = config.location;
        node.socialResource = config.socialResource;

        this.on("input", function(msg, send, done) {
            msg.name = node.name;
            msg.location = node.location;
            msg.socialResource = node.socialResource;

            send(msg);
            if (done) {
                done();
            }
        });
    }

    RED.nodes.registerType("socialService", SocialServiceNode);
};